     4→export function CombinationPrompt() {
     5→  const { currentCombo, isVisible } = useCombinations();
     6→
     7→  if (!currentCombo) return null;
     8→
     9→  return (
    10→    <div
    11→      className={clsx(
    12→        'fixed inset-0 flex items-center justify-center pointer-events-none z-50',
    13→        'transition-opacity duration-300',
    14→        isVisible ? 'opacity-100' : 'opacity-0'
    15→      )}
    16→    >
    17→      <div
    18→        className={clsx(
