     1→export type TimerStatus = 'idle' | 'running' | 'paused';
     2→export type Phase = 'round' | 'rest';
     3→export type Theme = 'dark' | 'light';
     4→export type ComboGroup = 'basic' | 'standard' | 'withDefense' | 'long';
     5→
     6→export interface TimerState {
     7→  status: TimerStatus;
     8→  phase: Phase;
     9→  currentRound: number;
    10→  timeRemaining: number;
    11→  totalRounds: number;
    12→  roundDuration: number;
    13→  restDuration: number;
    14→  warningPlayed: boolean;
    15→}
    16→
    17→export interface Settings {
    18→  combosEnabled: boolean;
    19→  comboInterval: number;
    20→  comboGroups: Record<ComboGroup, boolean>;
    21→  warningThreshold: number;
    22→  theme: Theme;
    23→  volume: number;
    24→}
    25→
    26→export type TimerAction =
    27→  | { type: 'START' }
    28→  | { type: 'PAUSE' }
    29→  | { type: 'TOGGLE' }
    30→  | { type: 'RESET' }
    31→  | { type: 'TICK' }
    32→  | { type: 'SET_WARNING_PLAYED'; payload: boolean }
    33→  | { type: 'SET_TOTAL_ROUNDS'; payload: number }
    34→  | { type: 'SET_ROUND_DURATION'; payload: number }
    35→  | { type: 'SET_REST_DURATION'; payload: number };
    36→
    37→export type SettingsAction =
    38→  | { type: 'TOGGLE_COMBOS' }
    39→  | { type: 'SET_COMBO_INTERVAL'; payload: number }
    40→  | { type: 'TOGGLE_COMBO_GROUP'; payload: ComboGroup }
    41→  | { type: 'SET_WARNING_THRESHOLD'; payload: number }
    42→  | { type: 'SET_THEME'; payload: Theme }
    43→  | { type: 'SET_VOLUME'; payload: number };
