     1→import { useState, useEffect, useRef, useCallback, useMemo } from 'react';
     2→import { useTimerStore } from '../stores/timerStore';
     3→import { COMBINATIONS } from '../utils/constants';
     4→
     5→export function useCombinations() {
     6→  const status = useTimerStore((state) => state.status);
     7→  const phase = useTimerStore((state) => state.phase);
     8→  const currentRound = useTimerStore((state) => state.currentRound);
     9→  const timeRemaining = useTimerStore((state) => state.timeRemaining);
    10→  const roundDuration = useTimerStore((state) => state.roundDuration);
    11→  const combosEnabled = useTimerStore((state) => state.combosEnabled);
    12→  const comboInterval = useTimerStore((state) => state.comboInterval);
    13→  const comboGroups = useTimerStore((state) => state.comboGroups);
    14→
    15→  const [currentCombo, setCurrentCombo] = useState<string | null>(null);
    16→  const [isVisible, setIsVisible] = useState(false);
    17→  const lastComboTimeRef = useRef(-1); // -1 means no combo shown yet this round
    18→  const timeoutRef = useRef<number | null>(null);
    19→  const prevRoundRef = useRef(currentRound);
    20→  const prevPhaseRef = useRef(phase);
    21→
    22→  // Build filtered combo list based on enabled groups
    23→  const availableCombos = useMemo(() => {
    24→    const combos: string[] = [];
    25→    if (comboGroups.basic) combos.push(...COMBINATIONS.basic);
    26→    if (comboGroups.standard) combos.push(...COMBINATIONS.standard);
    27→    if (comboGroups.withDefense) combos.push(...COMBINATIONS.withDefense);
    28→    if (comboGroups.long) combos.push(...COMBINATIONS.long);
    29→    return combos;
    30→  }, [comboGroups]);
    31→
    32→  const showCombo = useCallback(() => {
    33→    if (availableCombos.length === 0) return;
    34→    const randomIndex = Math.floor(Math.random() * availableCombos.length);
    35→    setCurrentCombo(availableCombos[randomIndex]);
    36→    setIsVisible(true);
    37→
    38→    // Hide after 2 seconds
    39→    if (timeoutRef.current) {
    40→      clearTimeout(timeoutRef.current);
    41→    }
    42→    timeoutRef.current = window.setTimeout(() => {
    43→      setIsVisible(false);
    44→    }, 2000);
    45→  }, [availableCombos]);
    46→
    47→  useEffect(() => {
    48→    // Reset when entering a new round
    49→    if (
    50→      currentRound !== prevRoundRef.current ||
    51→      phase !== prevPhaseRef.current
    52→    ) {
    53→      prevRoundRef.current = currentRound;
    54→      prevPhaseRef.current = phase;
    55→      lastComboTimeRef.current = -1; // Reset for new round
    56→    }
    57→
    58→    // Only show combos during active rounds
    59→    if (
    60→      status !== 'running' ||
    61→      phase !== 'round' ||
    62→      !combosEnabled
    63→    ) {
    64→      setIsVisible(false);
    65→      return;
    66→    }
    67→
    68→    // Calculate elapsed time in current round
    69→    const elapsedTime = roundDuration - timeRemaining;
    70→
    71→    // Show combo immediately when round starts (no combo shown yet this round)
    72→    if (lastComboTimeRef.current === -1) {
    73→      lastComboTimeRef.current = 0;
    74→      showCombo();
    75→      return;
    76→    }
    77→
    78→    // Check if it's time to show a new combo based on interval
    79→    if (elapsedTime >= lastComboTimeRef.current + comboInterval) {
    80→      lastComboTimeRef.current = elapsedTime;
    81→      showCombo();
    82→    }
    83→  }, [status, phase, currentRound, timeRemaining, roundDuration, combosEnabled, comboInterval, showCombo]);
    84→
    85→  // Reset when timer resets
    86→  useEffect(() => {
    87→    if (status === 'idle') {
    88→      lastComboTimeRef.current = -1;
    89→      setIsVisible(false);
    90→      setCurrentCombo(null);
    91→    }
    92→  }, [status]);
    93→
    94→  // Cleanup
    95→  useEffect(() => {
    96→    return () => {
    97→      if (timeoutRef.current) {
    98→        clearTimeout(timeoutRef.current);
    99→      }
   100→    };
   101→  }, []);
   102→
   103→  return { currentCombo, isVisible };
   104→}
