   193→          comboGroups: {
   194→            ...state.comboGroups,
   195→            [group]: !state.comboGroups[group],
   196→          },
   197→        })),
   198→
   199→      setWarningThreshold: (threshold) => set({ warningThreshold: threshold }),
   200→
   201→      setTheme: (theme) => set({ theme }),
   202→
   203→      setVolume: (volume) => set({ volume }),
   204→    }),
   205→    {
   206→      name: 'boxing-timer-storage',
   207→      partialize: (state) => ({
   208→        // Only persist config and settings, not runtime state
   209→        totalRounds: state.totalRounds,
   210→        roundDuration: state.roundDuration,
   211→        restDuration: state.restDuration,
   212→        combosEnabled: state.combosEnabled,
   213→        comboInterval: state.comboInterval,
   214→        comboGroups: state.comboGroups,
   215→        warningThreshold: state.warningThreshold,
   216→        theme: state.theme,
   217→        volume: state.volume,
   218→      }),
   219→      onRehydrateStorage: () => (state) => {
   220→        // Set initial timeRemaining based on restored roundDuration
   221→        if (state) {
   222→          state.timeRemaining = state.roundDuration;
   223→        }
   224→      },
   225→    }
   226→  )
