     4→// Create audio context for generating sounds
     5→function createBellSound(audioContext: AudioContext, volume: number): void {
     6→  const oscillator = audioContext.createOscillator();
     7→  const gainNode = audioContext.createGain();
     8→
     9→  oscillator.connect(gainNode);
    10→  gainNode.connect(audioContext.destination);
    11→
    12→  oscillator.frequency.value = 800;
    13→  oscillator.type = 'sine';
    14→
    15→  gainNode.gain.setValueAtTime(volume, audioContext.currentTime);
    16→  gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.8);
    17→
    18→  oscillator.start(audioContext.currentTime);
    19→  oscillator.stop(audioContext.currentTime + 0.8);
    20→}
    21→
    22→function createWarningBeep(audioContext: AudioContext, volume: number): void {
    23→  const oscillator = audioContext.createOscillator();
    24→  const gainNode = audioContext.createGain();
    25→
    26→  oscillator.connect(gainNode);
    27→  gainNode.connect(audioContext.destination);
    28→
    29→  oscillator.frequency.value = 440;
    30→  oscillator.type = 'square';
    31→
    32→  gainNode.gain.setValueAtTime(volume * 0.5, audioContext.currentTime);
    33→  gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.15);
    34→
    35→  oscillator.start(audioContext.currentTime);
    36→  oscillator.stop(audioContext.currentTime + 0.15);
    37→}
    38→
    39→export function useSound() {
    40→  const status = useTimerStore((state) => state.status);
    41→  const phase = useTimerStore((state) => state.phase);
    42→  const currentRound = useTimerStore((state) => state.currentRound);
    43→  const timeRemaining = useTimerStore((state) => state.timeRemaining);
    44→  const roundDuration = useTimerStore((state) => state.roundDuration);
    45→  const warningPlayed = useTimerStore((state) => state.warningPlayed);
    46→  const warningThreshold = useTimerStore((state) => state.warningThreshold);
    47→  const volume = useTimerStore((state) => state.volume);
    48→  const setWarningPlayed = useTimerStore((state) => state.setWarningPlayed);
    49→
    50→  const audioContextRef = useRef<AudioContext | null>(null);
    51→  const prevPhaseRef = useRef(phase);
    52→  const prevTimeRef = useRef(timeRemaining);
    53→
    54→  const getAudioContext = useCallback(() => {
    55→    if (!audioContextRef.current) {
    56→      audioContextRef.current = new AudioContext();
    57→    }
    58→    return audioContextRef.current;
    59→  }, []);
    60→
    61→  const playBell = useCallback(() => {
    62→    const ctx = getAudioContext();
    63→    // Play bell sound twice for round end
    64→    createBellSound(ctx, volume);
    65→    setTimeout(() => createBellSound(ctx, volume), 300);
    66→  }, [getAudioContext, volume]);
    67→
    68→  const playWarning = useCallback(() => {
    69→    const ctx = getAudioContext();
    70→    createWarningBeep(ctx, volume);
    71→  }, [getAudioContext, volume]);
    72→
    73→  // Handle phase transitions
    74→  useEffect(() => {
    75→    if (status !== 'running') return;
    76→
    77→    // Phase changed - play bell
    78→    if (prevPhaseRef.current !== phase) {
    79→      playBell();
    80→      prevPhaseRef.current = phase;
    81→    }
    82→
    83→    // Check for warning threshold
    84→    if (
    85→      timeRemaining === warningThreshold &&
    86→      prevTimeRef.current !== warningThreshold &&
    87→      !warningPlayed
    88→    ) {
    89→      playWarning();
    90→      setWarningPlayed(true);
    91→    }
    92→
    93→    prevTimeRef.current = timeRemaining;
    94→  }, [status, phase, timeRemaining, warningThreshold, warningPlayed, playBell, playWarning, setWarningPlayed]);
    95→
    96→  // Play bell when workout starts
    97→  useEffect(() => {
    98→    if (status === 'running' && currentRound === 1 && phase === 'round') {
    99→      // Only play on initial start
   100→      if (timeRemaining === roundDuration) {
   101→        playBell();
   102→      }
   103→    }
   104→  }, [status, currentRound, phase, timeRemaining, roundDuration, playBell]);
   105→
   106→  // Cleanup
   107→  useEffect(() => {
   108→    return () => {
   109→      if (audioContextRef.current) {
   110→        audioContextRef.current.close();
   111→      }
   112→    };
   113→  }, []);
   114→
   115→  return { playBell, playWarning };
   116→}
