     1→import { create } from 'zustand';
     2→import { persist } from 'zustand/middleware';
     3→import type { TimerStatus, Phase, Theme, ComboGroup } from '../types/timer';
     4→import {
     5→  DEFAULT_TOTAL_ROUNDS,
     6→  DEFAULT_ROUND_DURATION,
     7→  DEFAULT_REST_DURATION,
     8→  DEFAULT_COMBO_INTERVAL,
     9→  DEFAULT_WARNING_THRESHOLD,
    10→} from '../utils/constants';
    11→
    12→interface TimerState {
    13→  // Timer state
    14→  status: TimerStatus;
    15→  phase: Phase;
    16→  currentRound: number;
    17→  timeRemaining: number;
    18→  warningPlayed: boolean;
    19→
    20→  // Timer config (persisted)
    21→  totalRounds: number;
    22→  roundDuration: number;
    23→  restDuration: number;
    24→
    25→  // Settings (persisted)
    26→  combosEnabled: boolean;
    27→  comboInterval: number;
    28→  comboGroups: Record<ComboGroup, boolean>;
    29→  warningThreshold: number;
    30→  theme: Theme;
    31→  volume: number;
    32→}
    33→
    34→interface TimerActions {
    35→  // Timer actions
    36→  start: () => void;
    37→  pause: () => void;
    38→  toggle: () => void;
    39→  reset: () => void;
    40→  tick: () => void;
    41→  setWarningPlayed: (played: boolean) => void;
    42→
    43→  // Config actions
    44→  setTotalRounds: (rounds: number) => void;
    45→  setRoundDuration: (duration: number) => void;
    46→  setRestDuration: (duration: number) => void;
    47→
    48→  // Settings actions
    49→  toggleCombos: () => void;
    50→  setComboInterval: (interval: number) => void;
    51→  toggleComboGroup: (group: ComboGroup) => void;
    52→  setWarningThreshold: (threshold: number) => void;
    53→  setTheme: (theme: Theme) => void;
    54→  setVolume: (volume: number) => void;
    55→}
    56→
    57→type TimerStore = TimerState & TimerActions;
    58→
    59→const initialState: TimerState = {
    60→  // Timer state
    61→  status: 'idle',
    62→  phase: 'round',
    63→  currentRound: 1,
    64→  timeRemaining: DEFAULT_ROUND_DURATION,
    65→  warningPlayed: false,
    66→
    67→  // Timer config
    68→  totalRounds: DEFAULT_TOTAL_ROUNDS,
    69→  roundDuration: DEFAULT_ROUND_DURATION,
    70→  restDuration: DEFAULT_REST_DURATION,
    71→
    72→  // Settings
    73→  combosEnabled: false,
    74→  comboInterval: DEFAULT_COMBO_INTERVAL,
    75→  comboGroups: {
    76→    basic: true,
    77→    standard: true,
    78→    withDefense: true,
    79→    long: true,
    80→  },
    81→  warningThreshold: DEFAULT_WARNING_THRESHOLD,
    82→  theme: 'dark',
    83→  volume: 0.7,
    84→};
    85→
    86→export const useTimerStore = create<TimerStore>()(
    87→  persist(
    88→    (set, get) => ({
    89→      ...initialState,
    90→
    91→      // Timer actions
    92→      start: () => set({ status: 'running' }),
    93→
    94→      pause: () => set({ status: 'paused' }),
    95→
    96→      toggle: () => {
    97→        const { status } = get();
    98→        set({ status: status === 'running' ? 'paused' : 'running' });
    99→      },
   100→
   101→      reset: () => {
   102→        const { roundDuration } = get();
   103→        set({
   104→          status: 'idle',
   105→          phase: 'round',
   106→          currentRound: 1,
   107→          timeRemaining: roundDuration,
   108→          warningPlayed: false,
   109→        });
   110→      },
   111→
   112→      tick: () => {
   113→        const state = get();
   114→        if (state.status !== 'running') return;
   115→
   116→        const newTime = state.timeRemaining - 1;
   117→
   118→        if (newTime > 0) {
   119→          set({ timeRemaining: newTime });
   120→          return;
   121→        }
   122→
   123→        // Time has run out, transition to next phase
   124→        if (state.phase === 'round') {
   125→          // Check if this was the last round
   126→          if (state.currentRound >= state.totalRounds) {
   127→            // Workout complete
   128→            set({
   129→              status: 'idle',
   130→              timeRemaining: 0,
   131→              warningPlayed: false,
   132→            });
   133→            return;
   134→          }
   135→          // Move to rest phase
   136→          set({
   137→            phase: 'rest',
   138→            timeRemaining: state.restDuration,
   139→            warningPlayed: false,
   140→          });
   141→        } else {
   142→          // Move from rest to next round
   143→          set({
   144→            phase: 'round',
   145→            currentRound: state.currentRound + 1,
   146→            timeRemaining: state.roundDuration,
   147→            warningPlayed: false,
   148→          });
   149→        }
   150→      },
   151→
   152→      setWarningPlayed: (played) => set({ warningPlayed: played }),
   153→
   154→      // Config actions
   155→      setTotalRounds: (rounds) => {
   156→        const { status } = get();
   157→        set({
   158→          totalRounds: rounds,
   159→          ...(status === 'idle' && {
   160→            currentRound: 1,
   161→            phase: 'round' as const,
   162→          }),
   163→        });
   164→      },
   165→
   166→      setRoundDuration: (duration) => {
   167→        const { status, phase } = get();
   168→        set({
   169→          roundDuration: duration,
   170→          ...(status === 'idle' && phase === 'round' && {
   171→            timeRemaining: duration,
   172→          }),
   173→        });
   174→      },
   175→
   176→      setRestDuration: (duration) => {
   177→        const { status, phase } = get();
   178→        set({
   179→          restDuration: duration,
   180→          ...(status === 'idle' && phase === 'rest' && {
   181→            timeRemaining: duration,
   182→          }),
   183→        });
   184→      },
   185→
   186→      // Settings actions
   187→      toggleCombos: () => set((state) => ({ combosEnabled: !state.combosEnabled })),
   188→
   189→      setComboInterval: (interval) => set({ comboInterval: interval }),
   190→
   191→      toggleComboGroup: (group) =>
   192→        set((state) => ({
   193→          comboGroups: {
   194→            ...state.comboGroups,
   195→            [group]: !state.comboGroups[group],
   196→          },
   197→        })),
   198→
   199→      setWarningThreshold: (threshold) => set({ warningThreshold: threshold }),
   200→
   201→      setTheme: (theme) => set({ theme }),
   202→
   203→      setVolume: (volume) => set({ volume }),
   204→    }),
   205→    {
   206→      name: 'boxing-timer-storage',
   207→      partialize: (state) => ({
   208→        // Only persist config and settings, not runtime state
   209→        totalRounds: state.totalRounds,
   210→        roundDuration: state.roundDuration,
   211→        restDuration: state.restDuration,
   212→        combosEnabled: state.combosEnabled,
   213→        comboInterval: state.comboInterval,
   214→        comboGroups: state.comboGroups,
   215→        warningThreshold: state.warningThreshold,
   216→        theme: state.theme,
   217→        volume: state.volume,
   218→      }),
   219→      onRehydrateStorage: () => (state) => {
   220→        // Set initial timeRemaining based on restored roundDuration
   221→        if (state) {
   222→          state.timeRemaining = state.roundDuration;
   223→        }
   224→      },
   225→    }
   226→  )
   227→);
   228→

