   224→                        type="range"
   225→                        min={MIN_COMBO_INTERVAL}
   226→                        max={MAX_COMBO_INTERVAL}
   227→                        step={5}
   228→                        value={comboInterval}
   229→                        onChange={(e) => setComboInterval(Number(e.target.value))}
