   231→                      />
   232→                      <div className="flex justify-between text-xs text-gray-500 mt-1">
   233→                        <span>{MIN_COMBO_INTERVAL}s</span>
   234→                        <span>{formatDuration(MAX_COMBO_INTERVAL)}</span>
   235→                      </div>
   236→                    </div>
   237→
   238→                    {/* Combo Groups */}
   239→                    <div>
   240→                      <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
   241→                        Combo Types
   242→                      </label>
   243→                      <div className="space-y-2">
   244→                        {COMBO_GROUPS.map(({ key, label, description }) => (
   245→                          <label
   246→                            key={key}
   247→                            className="flex items-center gap-3 cursor-pointer"
   248→                          >
   249→                            <input
   250→                              type="checkbox"
   251→                              checked={comboGroups[key]}
   252→                              onChange={() => toggleComboGroup(key)}
   253→                              className="w-4 h-4 rounded border-gray-300 text-green-600 focus:ring-green-500 dark:border-gray-600 dark:bg-gray-700"
   254→                            />
   255→                            <div className="flex-1">
   256→                              <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
   257→                                {label}
   258→                              </span>
   259→                              <span className="text-xs text-gray-500 dark:text-gray-500 ml-2">
   260→                                {description}
   261→                              </span>
   262→                            </div>
   263→                          </label>
   264→                        ))}
   265→                      </div>
   266→                    </div>
   267→
   268→                    {/* Punch Number Legend */}
   269→                    <div className="border-t border-gray-200 dark:border-gray-700 pt-4">
   270→                      <button
   271→                        onClick={() => setShowLegend(!showLegend)}
   272→                        className="flex items-center justify-between w-full text-sm font-medium text-gray-700 dark:text-gray-300"
   273→                      >
   274→                        <span>Punch Number Legend</span>
   275→                        {showLegend ? (
   276→                          <ChevronUp className="w-4 h-4" />
   277→                        ) : (
   278→                          <ChevronDown className="w-4 h-4" />
   279→                        )}
   280→                      </button>
   281→                      {showLegend && (
   282→                        <div className="mt-3 grid grid-cols-2 gap-2">
   283→                          {PUNCH_LEGEND.map(({ number, name }) => (
   284→                            <div
   285→                              key={number}
   286→                              className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400"
   287→                            >
   288→                              <span className="font-mono font-bold text-gray-900 dark:text-white w-4">
   289→                                {number}
   290→                              </span>
   291→                              <span>= {name}</span>
   292→                            </div>
   293→                          ))}
   294→                          <div className="col-span-2 mt-2 text-xs text-gray-500 dark:text-gray-500">
   295→                            Slip & Roll = Defensive movements
   296→                          </div>
   297→                        </div>
   298→                      )}
   299→                    </div>
   300→                  </>
   301→                )}
   302→
   303→                {/* Warning Threshold */}
   304→                <div>
   305→                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
   306→                    Warning Sound: {warningThreshold}s before end
   307→                  </label>
   308→                  <input
   309→                    type="range"
   310→                    min={MIN_WARNING_THRESHOLD}
   311→                    max={MAX_WARNING_THRESHOLD}
   312→                    step={5}
   313→                    value={warningThreshold}
   314→                    onChange={(e) => setWarningThreshold(Number(e.target.value))}
   315→                    className="w-full h-2 rounded-lg appearance-none cursor-pointer bg-gray-200 dark:bg-gray-700"
   316→                  />
   317→                  <div className="flex justify-between text-xs text-gray-500 mt-1">
   318→                    <span>{MIN_WARNING_THRESHOLD}s</span>
   319→                    <span>{formatDuration(MAX_WARNING_THRESHOLD)}</span>
   320→                  </div>
   321→                </div>
   322→              </div>
   323→            </div>
   324→          </div>
   325→        </div>
   326→      )}
   327→    </>
   328→  );
   329→}
