                <input
                  type="range"
                  min={0}
                  max={1}
                  step={0.1}
                  value={settings.volume}
                  onChange={(e) =>
                    dispatchSettings({ type: 'SET_VOLUME', payload: Number(e.target.value) })
                  }
                  className="w-full h-2 rounded-lg appearance-none cursor-pointer bg-gray-200 dark:bg-gray-700"
                />
              </div>

